            $attrs['seo_description'] = $item['name'];
            $attrs['seo_keywords'] = $item['name'];
            $product = Product::factory()->afterCreating(function (Product $product) {
                File::copy(public_path('images/icon8.svg'), resource_path('images/icon8.svg'));
                $product->addMedia(resource_path('images/icon8.svg'))->toMediaCollection('image');
            })->create($attrs);
            File::copy(public_path('images/'.$item['image']), resource_path('images/'.$item['image']));
            $product->addMedia(resource_path('images/'.$item['image']))->toMediaCollection('image');

            if (isset($item['background'])) {
                File::copy(public_path('images/'.$item['background']), resource_path('images/'.$item['background']));
                $product->addMedia(resource_path('images/'.$item['background']))->toMediaCollection('background');
            }

            if ($i < 12) {
                $product->categories()->sync([$category->id]);
            }
        }
    }
}
